package evmdis

import (
	"encoding/hex"
	"strings"
	"testing"

	"github.com/ethereum/go-ethereum/core/asm"
)

func TestDisassembler_Disassemble(t *testing.T) {
	type args struct {
		code string
	}
	tests := []struct {
		name        string
		d           *Disassembler
		args        args
		wantERCType string
		wantErr     bool
	}{
		{
			name:        "erc20/usdt",
			wantERCType: "erc20",
			d:           NewDisassembler(),
			args: args{
				code: "",
			},
			// want:    []evmops.Line{},
			wantErr: false,
		},
		{
			name:        "erc20/uniswap",
			wantERCType: "erc20",
			args: args{
				code: "",
			},
		},
		{
			name: "erc721/ENS",
			// we expect error for ENS as the error is caused by the official asm package
			wantErr:     true,
			wantERCType: "ERC721",
			args: args{
				code: "",
			},
		},
		{
			name: "erc20/goerli_test_token",
			args: args{
				code: "",
			},
			wantERCType: "ERC20",
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			d := &Disassembler{}
			got, err := d.Disassemble(tt.args.code)
			if (err != nil) != tt.wantErr {
				t.Errorf("Disassembler.Disassemble() error = %v, wantErr %v, compiledLen=%d", err, tt.wantErr, got.CompiledLen)

				script, err := hex.DecodeString(tt.args.code)
				if err != nil {
					t.Logf("hex.DecodeString() error = %v", err)
				}

				_, err = asm.Disassemble(script)
				if err != nil {
					t.Logf("asm.Disassemble() error = %v", err)
				} else {
					t.Log("asm.Disassemble() error = nil")
				}

				if strings.HasPrefix(tt.name, "erc20") {
					t.Logf("erc20 supported = %v", got.Implements(ERC20Iface))
				}

				if strings.HasPrefix(tt.name, "erc721") {
					t.Logf("erc721 supported = %v", got.Implements(ERC721Iface))
				}

				return
			}

			if tt.wantERCType == "erc20" && !got.Implements(ERC20Iface) {
				t.Errorf("expected correct detection of erc20")
			}

			if tt.wantERCType == "erc721" && !got.Implements(ERC721Iface) {
				t.Errorf("expected correct detection of erc721")
			}
		})
	}
}
